     let left_part=document.getElementById("left_part");
     let right_part=document.getElementById("right_part");
     companies();
     jobApplicationDetails(0);
   function companies(){
     jobData.map((element,index)=>{
         let shift="";
         element.shift.map((data)=>{
         shift+=`<li>${data}</li>`
      })

     left_part.innerHTML+=`
         <div class="data">
            <div class="left-part_left">
               <img src="${element.url}" alt="no image">
            </div>
            <div class="left_part_right">
               <h1 id="left_part_right_job_rol">${element.role}</h1>
               <p id="left_part_right_company_name">${element.companyName}</p>
               <p id="left_part_right_company_location"> ${element.location}</p>
               <p id="left_part_right_shift">${shift}</p>
               
            </div> 
        </div>
   `
     })
   }
      
   let data=document.getElementsByClassName("data");
    for(let j=0;j<data.length;j++){
      data[j].addEventListener("click",()=>{
         jobApplicationDetails(j)
    })
   }
   
    function jobApplicationDetails(val){
      let shiftTime="";
      let skill="";
      if(screen.width>600){
      for(let i=0;i<jobData[val].shift.length;i++){
           shiftTime+=`<span>${jobData[val].shift[i]}</span>`
      }
      for(let i=0;i<jobData[val].skillReq.length;i++){
         skill+=jobData[val].skillReq[i]+" ,";
    }
      right_part.innerHTML=`
      <h1>${jobData[val].role}</h1>
      <p>${jobData[val].companyName} . <span> ${jobData[val].location}</span></p>
      <p class="company_location_name">
          <i class="fa-solid fa-briefcase"></i>
          <span class="working_shift">
          ${shiftTime}
          </span>
      </p>
      <p  class="company_skill"><i class="fa-solid fa-check"></i>skills: <span>${skill}</span></p>
  
      <div class="btn_row">
          <button class="btn_apply" onClick=apply(${val})>Apply<i class="fa-solid fa-phone"></i></button>
          <button class="btn_save">Save</button>
      </div>
      <div class="about_job">
          <h2>about job</h2>
          <div>${jobData[val].jobResponsibilities}</div>
      </div>
      <div class="about_company">
          <h2>about company</h2>
          <div>${jobData[val].companyDescription}</div>
      </div>
      `
   }
    }

    function apply(val){
      if(jobData[val].jobUrl!=""){
         window.location=jobData[val].jobUrl;
      }else{
         swal("error!", "Application is closed please try new job!", "error")
      }
       
   }
    


 